import { useState } from "react"

export function switchforward (props) {

    const [displayedImg, setCurrentDisplayedImg] = useState([]);

    if (props.length - 1) {
          setCurrentDisplayedImg(displayedImg)
    } else {
               setCurrentDisplayedImg(displayedImg + 1)
    }
  }


  export function switchback () {
   if (props.length - 1 > 0) {
          setCurrentDisplayedImg(displayedImg - 1)
    } else {
               setCurrentDisplayedImg(displayedImg)
    }
  }
