import { useState } from "react"
import VectorLeft from "../../assets/VectorLeft.png"
import VectorRight from "../../assets/VectorRight.png"
import styled from 'styled-components';


const CarousselWrap = styled.div `
    display: flex; 
    flex-direction: column; 
    background-color: #FF6060;
    height:415px; 
    width: 1240px; 
`

const CarousselCover = styled.img `
    width: 100%;
    height: 100%;
    object-fit: cover;
`
const Arrows = styled.div `
    display: flex; 
    justify-content: space-between; 
    align-item: center; 
    margin-bottom: -80px; 

`

const img1 = styled.img `
margin-bottom: -80px; 
`

function Caroussel(props) {

const [displayedImg, setCurrentDisplayedImg] = useState(0);

return (

    <CarousselWrap>
        <CarousselCover src={props[displayedImg]} alt="Carousel cover"/>

                <img1 src={VectorLeft} alt="arrow left"  onClick={() => displayedImg > 0 && setCurrentDisplayedImg(displayedImg - 1)  }/>
                <img1 src={VectorRight} alt="arrow right" onClick={() => displayedImg < props.length && setCurrentDisplayedImg(displayedImg + 1) }/>

    </CarousselWrap>
)

}

export default Caroussel
